import React from 'react';
import ReactMarkdown from 'react-markdown';

import ArticleContent from '../Components/Articles/ArticleContent';
import HeaderArticle from '../Components/Header/Header-Article-Body';

const ArticleBody = ({match}) => {
    const name = match.params.name;
    const article = ArticleContent.find(article => article.name === name);
    const otherArticles = ArticleContent.find(otherArticles => article.name != name);

    return (
        <>
            <HeaderArticle />
            <div className="article-body-container">
                <div className="article-body-title">
                    <h1>{article.title}</h1>
                </div>
                <div className="article-body-content">
                    <ReactMarkdown>
                        {article.content}
                    </ReactMarkdown>
                </div>
                <div className="article-body-date">
                    <p>{article.date}</p>
                </div>
                <div>
                    <ul>
                        <li>{otherArticles}</li>
                    </ul>
                </div>
            </div>
        </>
)};

export default ArticleBody;
